    const [state,setState]=useState({
        input:'2',
        input2:'1'
    })
  
    const handleChange =(e)=>{
            const {name,value}=e.target;
            setState({...state,[name]:value})

    }
   useEffect(()=>{
        console.log('from useEffect');
   },[])

    return (
        <div>
            <form>
                <input type="text" name="input" value={state.input} onChange={handleChange} />
                <input type="text" name="input2" value={state.input2} onChange={handleChange} />
            </form>
        </div>
    )
}
