import React, {Component} from "react"

class App extends Component{
    constructor(){
        super()
        this.state={
            firstName:""
        }
    }
    handleChange(){
        this.setState({
          firstName: "???"
        })
    }
    render(){
        return(
            <text>
                <input type="text" placeholder="First Name" onChange={this.handleChange} />
            </text>
        )
    }
}
export default App
