import React, {Component} from "react"

class App extends Component{
    constructor(){
        super()
        this.state={
            firstName:"",
            lastName:"",
            isFriendly:true,
            gender: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }
    handleChange(event){
        const{name, value, type, checked} = event.target
      type === "checkbox"?this.setState({
          [name]: checked
        }):this.setState({[name]: value})
    }
    render(){
        return(
            <form>
                <input type="text" name="firstName" value={this.state.firstName} placeholder="First Name" onChange={this.handleChange} />
                <br />
                <input type="text" name="lastName" value={this.state.lastName} placeholder="Last Name" onChange={this.handleChange} />
                <textarea value="some value" onChange={this.handleChange}/>
                <br />
                <label>
                <input type="checkbox" name="isFriendly" checked={this.state.isFriendly} onChange={this.handleChange} /> Is Friendly?
                </label>
                <label>
                <input type="radio" name="gender" checked={this.state.gender === "male"} onChange={this.handleChange} value="male" /> Male
                </label>
                <label>
                <input type="radio" name="gender" checked={this.state.gender === "female"} onChange={this.handleChange} value="female" /> Female
                </label>
                <h1>{this.state.firstName} {this.state.lastName}</h1>
            </form>
        )
    }
}
export default App
