import React, { Component } from 'react'

export default class Form extends Component {
    
    constructor(props){
        super(props);
        this.state={
            input:'test',
            input2:'test2'
        }
        
        this.handleChange=this.handleChange.bind(this)

    }
    handleChange(e){
       const newInput=e.target.value;
        const key=e.target.name
        console.log(key,newInput);
        this.setState({[key]:newInput})
    }
   
    handleSubmit(e){
        e.preventDefault()
        console.log(this.state);
        
    }
  
    render() {
        return (
            <div>
               <form onSubmit={this.handleSubmit.bind(this)} >
               <input onChange={this.handleChange} name="input" value={this.state.input} type="text" placeholder="input" />
               <input onChange={this.handleChange} name="input2" value={this.state.input2} type="text" placeholder="input" />
                <button type="submit" >ok</button>
               </form>
            </div>
        )
    }
}
