import React, { Component } from 'react'

export default class Form extends Component {
    constructor(props){
        super(props);
        this.state={
            input:'test'
        }

    }
    handleChange=(e)=>{
       const newInput=(e.target.value);
        this.setState({input:newInput})
    }
    render() {
        return (
            <div>
               <form >
               <input onChange={this.handleChange} value={this.state.input} type="text" placeholder="input" />
                <button type="submit" >ok</button>
               </form>
            </div>
        )
    }
}
