import {useState} from 'react'

export default function Hooks() {
    const [state,setState]=useState({
        input:''
    })
    const handleChange =(e)=>{
            const {name,value}=e.target;
            setState({[name]:value})

    }
    return (
        <div>
            <form>
                <input type="text" name="input" value={state.input} onChange={handleChange} />
            </form>
        </div>
    )
}
