import React,{useState,useEffect} from "react"
 import Header from "./Header"
 import MemeGenerator from "./MemeGenerator"
 function App(){
   const [count, setCount] = useState(0);
   const [count1, setCount1] = useState(0);
   const [count2, setCount2] = useState(0);
   const handleClick=(value)=>{
       
      return ()=>{
        switch (value){
          case '1':setCount(count+1);break;
          case '2':setCount1(count1+1);break;
          default:setCount2(count2+1);break;
        }
      }
   }
   
   useEffect(()=>{
      console.log('component is rerender',count2);

   },[count,count1])
   return(

     <div>
       <p>value 1: {count},value 2: {count1},value 3: {count2} </p>
       <button onClick={handleClick('1')}>click1</button>
       <button onClick={handleClick('2')}>click2</button>
       <button onClick={handleClick('null')}>click3</button>
     </div>
   )
 }
 const ShowCounter=({count})=>{
   return <p>props: {count}</p>
 }
 export default App
